// src/components/HomePage.js
import React from 'react';
import { Link } from 'react-router-dom';

function HomePage() {
  const profileName = localStorage.getItem('profileName') || 'Guest';

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', marginTop: '50px' }}>
      <div style={{
        border: '1px solid #ddd', 
        borderRadius: '8px', 
        padding: '20px 40px', 
        textAlign: 'center', 
        boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
        maxWidth: '500px'
      }}>
        <h1 style={{ fontSize: '2em', marginBottom: '10px' }}>Welcome to WiseShare</h1>
        <p style={{ fontSize: '1.2em', color: '#555' }}>Hello, {profileName}!</p>
        <Link to="/signup" style={{ fontSize: '1em', color: '#007bff', textDecoration: 'none', marginTop: '20px' }}>
          Sign Up
        </Link>
      </div>
    </div>
  );
}

export default HomePage;
